local MAP_SCAN_DIR = '../'local t_Maps = {}local t_Data = {}function love.load ()    for _,strFile in ipairs (love.filesystem.getDirectoryItems(MAP_SCAN_DIR)) do        if (strFile:sub(#strFile-4, #strFile) == '.png') then            local image = love.graphics.newImage (MAP_SCAN_DIR .. strFile)            if (image) then                table.insert (t_Maps, image)            end        end    end        t_Data.menu = {}    endfunction love.draw ()    endfunction love.update ()    endfunction love.mousepressed (intX, intY, strButton)end